input = ""
# input = "12345678"
# input = "03036732577212944063491565474664"
input = input.chars.map(&:to_i)
input = input * 10_000

PATTERN_BITS = [0, 1, 0, -1]

100.times do |phase|
  input = input.length.times.map do |position|
    puts "phase #{phase + 1} position #{position + 1}"

    pattern_state = 0
    pattern_index = 0

    input.map do |i|
      pattern_index += 1
      if pattern_index > position
        pattern_index = 0

        pattern_state += 1
        pattern_state %= 4
      end

      pattern_bit = PATTERN_BITS[pattern_state]

      i * pattern_bit
    end.sum.abs % 10
  end
end

p input

offset = input.first(7).join("").to_i
message = input[offset, 8]

p message
